<template>\n  <div class=\"p-4\">\n    <input v-model=\"query\" @keyup.enter=\"fetchRecipes\" placeholder=\"Search recipes\" class=\"border p-2 w-full\" />\n    <div class=\"grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4 mt-4\">\n      <RecipeCard v-for=\"r in recipes\" :key=\"r.id\" :recipe=\"r\" />\n    </div>\n  </div>\n</template>\n\n<script setup>\nimport { ref } from 'vue';\nimport RecipeCard from './RecipeCard.vue';\nconst query = ref('');\nconst recipes = ref([]);\n\n// Fetch recipes from public API\nasync function fetchRecipes() {\n  const res = await fetch(`https://api.example.com/search?q=${query.value}`);\n  recipes.value = (await res.json()).results;\n}\n</script>